/*Fonction pour créer le message*/
function creationMessage(carnetBord) {
  carnetBord.titre.value = carnetBord.subject.value;

  carnetBord.message.value = `<div class="fiche f-pres f-carnet ${carnetBord.groupe.value}"><div class="fiche__identity-section f-carnet__identity-section"><div class="fiche__head"><div class="fiche__name">${carnetBord.presNom.value}</div><div class="fiche__icon-container"><img class="fiche__icon f-pres__icon" src="${carnetBord.presAvatarImg.value}"></div></div><div class="fiche__idblock"><div class="fiche__nickname">${carnetBord.presNick.value}</div><div class="fiche__identity f-carnet__identity"><div class="fiche__field"><p class="fiche__field-label">Age</p><p class="fiche__field-content">${carnetBord.presAge.value}</p></div><div class="fiche__field"><p class="fiche__field-label">Genre</p><p class="fiche__field-content">${carnetBord.presGenre.value}</p></div><div class="fiche__field"><p class="fiche__field-label">Origine</p><p class="fiche__field-content">${carnetBord.presOrigine.value}</p></div><div class="fiche__field"><p class="fiche__field-label">Métier</p><p class="fiche__field-content">${carnetBord.presMetier.value}</p></div><div class="fiche__field"><p class="fiche__field-label">Allégeance</p><p class="fiche__field-content">${carnetBord.presAllegeance.value}</p></div><div class="fiche__field"><p class="fiche__field-label">Relation au Bliss</p><p class="fiche__field-content">${carnetBord.presRelBliss.value}</p></div><div class="fiche__field--resume f-pres__field--resume"><p class="fiche__field-label">Résumé</p><p class="fiche__field-content">${carnetBord.presResume.value}</p></div></div></div></div><div class="fiche__section f-pres__char-section">${(carnetBord.carnetLien1.value && carnetBord.carnetLien1Desc.value || carnetBord.carnetLien2.value && carnetBord.carnetLien2Desc.value || carnetBord.carnetLien3.value && carnetBord.carnetLien3Desc.value ) ? `<div class="f-carnet__links-types">${(carnetBord.carnetLien1.value && carnetBord.carnetLien1Desc.value) ? `<div class="fiche__field"><p class="fiche__field-label">${carnetBord.carnetLien1.value}</p><p class="fiche__field-content fiche__field-content--bordered">${carnetBord.carnetLien1Desc.value}</p></div>` : ''}${(carnetBord.carnetLien2.value && carnetBord.carnetLien2Desc.value) ? `<div class="fiche__field"><p class="fiche__field-label">${carnetBord.carnetLien2.value}</p><p class="fiche__field-content fiche__field-content--bordered">${carnetBord.carnetLien2Desc.value}</p></div>` : ''}${(carnetBord.carnetLien3.value && carnetBord.carnetLien3Desc.value) ? `<div class="fiche__field"><p class="fiche__field-label">${carnetBord.carnetLien3.value}</p><p class="fiche__field-content fiche__field-content--bordered">${carnetBord.carnetLien3Desc.value}</p></div>` : ''}</div>` : ''}<div class="f-carnet__rp-tracking"><div class="title title--s f-carnet__rp-tracking-title">Suivi RP</div><div class="f-carnet__rp-tracking-text">${carnetBord.carnetSuivi.value}</div></div></div><div class="fiche__section fiche__section--chrono"><div class="fiche__head"><div class="fiche__name">Chronologie & Histoire</div></div><div class="fiche__chrono">${carnetBord.carnetChrono.value}</div></div></div>`;
}