/*Fonction pour créer le message*/
function creationMessage(reseauxSociaux) {
  reseauxSociaux.titre.value = reseauxSociaux.subject.value;

  reseauxSociaux.message.value = `<div class="fiche ressoc"><div class=ressoc__body><div class=ressoc__banner><img class=ressoc__banner-img src=assets/socials-banner.png alt=Banner><div class=ressoc__icon><img class=ressoc__icon-img src=assets/inviteavatar.jpg alt=Icon></div></div><div class=ressoc__intro><div class=ressoc__heading><h2 class="ressoc__pseudo title title--s">${reseauxSociaux.resPseudo.value}</h2><p class=ressoc__handle>@${reseauxSociaux.resHandle.value}</div><div class="fiche__field ressoc__bio"><div class=fiche__field-label>Bio</div><div class=fiche__field-content>${reseauxSociaux.resBio.value}</div></div><div class=ressoc__recap><div class=ressoc__images><img class=ressoc__image src=${reseauxSociaux.resImg1.value}> <img class=ressoc__image src=${reseauxSociaux.resImg2.value}> <img class=ressoc__image src=${reseauxSociaux.resImg3.value}> <img class=ressoc__image src=${reseauxSociaux.resImg4.value}></div><div class=ressoc__stats-list><div class=ressoc__stat-line>${reseauxSociaux.resAbonnements.value} abonnements</div><div class=ressoc__stat-line>${reseauxSociaux.resAbos.value} abonnés</div><div class=ressoc__stat-line>${reseauxSociaux.resPosts.value}</div><div class=ressoc__stat-line>${reseauxSociaux.resLikes.value} Likes</div></div></div></div></div></div>`;
}